                if (window.device) {
                    navigator.notification.alert(
                        'Missing Email Id', // message
                        null, // callback
                        'Error', // title
                        'OK' // buttonName
                    );
                } else {
                    alert('Missing Email Id')
                }

                return;
            }
            if ($scope.password == '') {

                if (window.device) {
                    navigator.notification.alert(
                        'Missing password', // message
                        null, // callback
                        'Error', // title
                        'OK' // buttonName
                    );
                } else {
                    alert('Missing password')
                }

                return;
            }
            if ($scope.username.indexOf('@') !== -1) {
                if ($scope.username.split('@')[1] !== $rootScope.user.tenantdomain) {
                    if (window.device) {
                        navigator.notification.alert(
                            'Invalid user domain', // message
                            null, // callback
                            'Error', // title
                            'OK' // buttonName
                        );
                    } else {
                        alert('Invalid user domain')
                    }
                    return;
                }
            } else {
                $scope.username = $scope.username + '@' + $rootScope.user.tenantdomain;
            }

            setTimeout(function() {
                $.blockUI();
                window['authFunction']($scope.username, $scope.password, $http, $rootScope, authSuccess, authFailure)
            }, 500);

        };


        $scope.openUrl = function(url) {
            window.open(url, '_blank');
        }

        $scope.$on("$destroy", function() {
            $(".backButtonArrow").show();
        });

        $scope.openForgotPass = function() {
            if ($scope.forgotPass) {
                window.open($scope.forgotPass, '_blank');
            } else {
                window.open('https://pwms.quicklaunchsso.com/api/open/showOpenChangePassword?tenantDomain=' + $rootScope.user.tenantdomain, '_blank');
            }
        }

    } catch (e) {
        alert(e)
    }

}

