        "image": val.imglink
      }]
    };
    items.push(item);
  });

  items.sort(function(a, b) {
    if (a.name < b.name)
      return -1;
    if (a.name > b.name)
      return 1;
    return 0;
  });
  $scope.items = items;
  $rootScope.items = items;


  $scope.walkdirection = "btn-primary";
  $scope.cycledirection = "";
  $scope.drivedirection = "";

  $scope.selectitem = function() {
    $("#building_selector")
      .blur();

    if ($scope.campusLocation) {
      var item = $scope.campusLocation;
      $rootScope.locationSelected = null;
      $scope.campusLocation = null;
      $scope.campusLocationDir = true;
    } else {
      var item = $scope.items[$scope.selitem];
    }
    if (item) {
      $scope.curitem = item;
      try {
        var cb = function(loaded) {

          var geocoder;
          var placeCoordinate;
          var errorCallback = function(error) {
            $.unblockUI();
            navigator.notification.alert("Unable to find your location. Please check your GPS settings, network connectivity and Location services.", function() {}, 'Alert', 'Ok');
            $scope.trackinginfo = "Unable to find your location. Please check your GPS settings, network connectivity and Location services." + error.message + " ( " + error.code + " )";
            $scope.$digest();
          }
          var placeGeoCoder = function(results) {
            try {
              $.unblockUI();

              console.log(JSON.stringify(results));
              placeCoordinate = results;
              $rootScope.mylocation = results;
              var latlng = new google.maps.LatLng(item.clat, item.clong);
              item
              var myOptions = {
                zoom: 18,
                center: latlng,
                mapTypeId: google.maps.MapTypeId.ROADMAP
              };
              var mapDisplay = document.getElementById("MapMapCanvas")
              var map = new google.maps.Map(mapDisplay, myOptions);
              var myloc = new google.maps.Marker({
                position: latlng,
                map: map,
                title: "title"
              });
              map.setTilt(45);
              $scope.markers.push(myloc);
              $scope.map = map;
              $scope.walk();
            } catch (e) {
              $.unblockUI();
              alert("Error : " + e);
            }
          }

          $scope.placeGeoCoder = placeGeoCoder;
          $scope.errorCallback = errorCallback;
          if (navigator.geolocation) {
            $.blockUI();
            navigator.geolocation.getCurrentPosition(placeGeoCoder, errorCallback, {
              timeout: 10000,
              frequency: 3000,
              enableHighAccuracy: true
            });
          } else {
            alert("not working")
          }
        };
        window.cb = cb;
        if (typeof google === 'object' && typeof google.maps === 'object') {
          window.cb(true);
        } else {
          var script = document.createElement("script");
          script.type = "text/javascript";
          script.src = "https://maps.google.com/maps/api/js?sensor=false&callback=cb";
          document.body.appendChild(script);
          //$.getScript("https://maps.googleapis.com/maps/api/js?key=AIzaSyDUbP_Qxh9wbPOvgW8iH_zuLu8K3fSlv0I&sensor=false&callback=cb");
        }

      } catch (e) {
        alert(e);
      }

    }
  }

  $scope.travelmode = "Walk";
  $scope.walk = function() {
    $scope.travelmode = "Walk";
    $scope.curtravelmode = "walk";
    $scope.walkdirection = "btn-primary";
    $scope.cycledirection = "";
    $scope.drivedirection = "";
    $scope.directiondisplay("walk", $scope.map, $rootScope.mylocation.coords.latitude, $rootScope.mylocation.coords.longitude, $scope.curitem.clat, $scope.curitem.clong);
    //$scope.directiondisplay("walk",$scope.map, "34.289408","-83.813368", $scope.curitem.clat, $scope.curitem.clong );

  }

  $scope.bicycle = function() {
    $scope.travelmode = "Bicycle";
    $scope.curtravelmode = "cycle";
    $scope.walkdirection = "";
    $scope.cycledirection = "btn-primary";
    $scope.drivedirection = "";
    $scope.directiondisplay("cycle", $scope.map, $rootScope.mylocation.coords.latitude, $rootScope.mylocation.coords.longitude, $scope.curitem.clat, $scope.curitem.clong);
    //$scope.directiondisplay("cycle",$scope.map, "34.289408","-83.813368", $scope.curitem.clat, $scope.curitem.clong );
  }

  $scope.drive = function() {
    $scope.travelmode = "Drive";
    $scope.curtravelmode = "drive";
    $scope.walkdirection = "";
    $scope.cycledirection = "";
    $scope.drivedirection = "btn-primary";
    $scope.directiondisplay("drive", $scope.map, $rootScope.mylocation.coords.latitude, $rootScope.mylocation.coords.longitude, $scope.curitem.clat, $scope.curitem.clong);
  }


  $scope.directiondisplay = function(mode, map, slat, slng, elat, elng) {
    $scope.trackinginfo = "";
    if ($scope.directionsDisplay) {
      $scope.directionsDisplay.setMap(null);
    }
    /*for (i in $scope.markers) {
        $scope.markers[i].setMap(null);
    }*/
    var directionsDisplay = new google.maps.DirectionsRenderer({
      suppressMarkers: false
    });
    var icons = {
      start: new google.maps.MarkerImage(

        // URL
        'images/start-pin.png',
        // (image width,height)
        new google.maps.Size(32, 32),
        //Point for the Image
        new google.maps.Point(0, 0)
      ),
      end: new google.maps.MarkerImage(
        // URL
        'images/end-pin.png',
        // (image width,height)
        new google.maps.Size(32, 32),
        //Point for the Image
        new google.maps.Point(0, 0)
      )
    };
    directionsDisplay.setMap(null);
    directionsDisplay.setMap(map);
    $scope.directionsDisplay = directionsDisplay;

    try {

      var directionsService = new google.maps.DirectionsService();
      var start = new google.maps.LatLng(slat, slng);
      var end = new google.maps.LatLng(elat, elng);
      var travelModeOption = mode;
      var request = {
        origin: start,
        destination: end,
        travelMode: "",
        unitSystem: google.maps.UnitSystem.METRIC
      };

      switch (travelModeOption) {
        case "walk":
          {
            var usertravelMode = "WALKING";
            request.travelMode = google.maps.TravelMode.WALKING;
            break;
          }
        case "cycle":
          {
            var usertravelMode = "BICYCLING";
            request.travelMode = google.maps.TravelMode.BICYCLING;
            break;
          }
        case "drive":
          {
            var usertravelMode = "DRIVING";
            request.travelMode = google.maps.TravelMode.DRIVING;
            break;
          }
        default:
          {
            var usertravelMode = "WALKING";
            request.travelMode = google.maps.TravelMode.WALKING;
            break;
          }
      }
    } catch (e) {
      alert(e)
    }
    directionsService.route(request, function(result, status) {
      if (status == google.maps.DirectionsStatus.OK) {
        try {
          for (i in $scope.markers) {
            $scope.markers[i].setMap(null);
          }
          var len = result.routes[0].legs[0].distance.text.length - 3;
          var miles = (result.routes[0].legs[0].distance.value) * 0.000621;
          var d = miles.toString();
          $scope.distmiles = d.slice(0, 6);
          $scope.duration = JSON.stringify(result.routes[0].legs[0].duration.text);
        } catch (e) {}
        for (i in $scope.markers) {
          $scope.markers[i].setMap(null);
        }
        directionsDisplay.setDirections(result);
        $scope.$digest();
      } else {
        //navigator.notification.alert("Oops! Your device dont have GPS enabled " + status, function() {}, 'Alert', 'Ok');
        $scope.trackinginfo = "Unable to get GPS data. ( " + status + " )";
        $scope.$digest();
      }
    });
  }
  var sel = $rootScope.locationSelected;
  if (sel) {
    var campusLocations = $.parseJSON(pagedef.mapmarkers)[sel];
    var item = {
      "name": campusLocations.name,
      "clat": campusLocations.latitude,
      "clong": campusLocations.longitude,
      "zoom": "16",
      "markers": [{
        "lat": campusLocations.latitude,
        "long": campusLocations.longitude,
        "desc": campusLocations.name
      }]
    };
    $scope.campusLocation = item;
    if ($scope.campusLocation) {
      $scope.selectitem();
    }
  }

}

function pageprocessorYouTube20(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location,services,studioParam) {
  $scope.studioParam = studioParam;
  $.blockUI()
  $("#fixedFooterContent").html('');
  var url = "https://www.googleapis.com/youtube/v3/search?videoEmbeddable=true&order=date&part=snippet&channelId=" + pagedef.url + "&type=video&maxResults=50&key=AIzaSyCsgCy_wu22gBBf-jewPk76QN9mUzevkGw";

  $http({
    'url': url,
    'method': 'GET'
  }).then(function successCallBack(data) {
    $.unblockUI();
    var data = data.data;
    if (data.items.length == 0) {
      if (window.device) {
        navigator.notification.alert(
          'No video found for given channel id.',
          null,
          '',
          'Ok'
        );
      } else
        alert('No video found for given channel id.');

      window.history.back();
      return;
    }
    for (var i = 0; i < data.items.length; i++) {
      data.items[i].playing = false;
    }

    $scope.videos = data.items;
    $scope.videos[0].playing = true;
    $rootScope.videos = data.items;
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.loadVideo(0);

  }, function errorCallBack(data) {
    $.unblockUI();
    if (window.device) {
      navigator.notification.alert(
        'Incorrect channel id.',
        null,
        '',
        'Ok'
      );
    } else
      alert('Incorrect channel id');
    window.history.back();
  });

  $scope.navigate = function(index) {
    if ($rootScope.webApp != undefined) {
      $scope.loadVideo(index);
    } else {
      $rootScope.index = index;
      $location.path("/app/YouTube20/YouTube20VideosDetails");
    }
      var scroll_pos=($('#myHeader').position().top);
      $("#appContent , #pageContent").animate({ scrollTop: (scroll_pos) }, "slow");
  }


  $scope.loadVideo = function(index) {
    for (var i = 0; i < $scope.videos.length; i++) {
      $scope.videos[i].playing = false;
    }
    var video = $scope.videos[index];

    video.playing = true;
    var videoID = video.id.videoId;
    $scope.publishDate = videoID.publishedAt;
    var url = "https://www.googleapis.com/youtube/v3/videos?id=" + videoID + "&key=AIzaSyCsgCy_wu22gBBf-jewPk76QN9mUzevkGw&part=snippet,contentDetails,statistics,status";
    $http({
      'method': 'GET',
      'url': url
    }).then(function successCallBack(data) {
      var data = data.data;
      $scope.title = data.items[0].snippet.localized.title;
      $scope.publishDate = data.items[0].snippet.publishedAt;
      $scope.trustSrc = function() {
        return $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID + "?rel=0&autoplay=1");
      };
      $scope.description = data.items[0].snippet.localized.description;
    });
  }
}

function pageprocessorYouTube20VideosDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  try {

    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    try {
      var onPause = function() {
        $("iframe").attr("src", $("iframe").attr("src"));
      };
      document.addEventListener("pause", onPause, false);
    } catch (e) {}
    $scope.showDetails = false;
    $scope.hideDetails = false;
    $scope.showDescription = function() {
      $scope.showDetails = true;
    }

    $scope.loadVideo = function(index) {
      for (var i = 0; i < $rootScope.videos.length; i++) {
        $rootScope.videos[i].playing = false;
      }

      $rootScope.videos[index].playing = true;
      var video = $rootScope.videos[index];
      var videoID = video.id.videoId;

      $scope.trustSrc = $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID + "?rel=0");

      var url = "https://www.googleapis.com/youtube/v3/videos?id=" + videoID + "&key=AIzaSyCsgCy_wu22gBBf-jewPk76QN9mUzevkGw&part=snippet,contentDetails,statistics,status";
      $http({
        'url':url,
        'method':'GET'}).then(function successCallBack(data) {
        var data = data.data;
        $scope.title = data.items[0].snippet.localized.title;
        $scope.publishDate = data.items[0].snippet.publishedAt;
        $scope.description = data.items[0].snippet.localized.description;
        $('.scrollable-content').animate({
                    scrollTop: 0
                }, 'slow');
      },function errorCallBack(data) {
        $.unblockUI();
      });
    }

    $scope.loadVideo($rootScope.index);
  } catch (e) {
    console.log(e);
  }
}

